import React from "react";
import useShow from "../../hooks/useShow";
import "./index.scss";

function FilterWidget({ children, title }) {
  const { ref, style, show } = useShow();

  function handleClick() {
    show();
  }

  return (
    <div className="filter-widget">
      <h2 className="filter-widget__title" onClick={handleClick}>
        {title}
      </h2>
      <div style={{ ...style }}>
        <div ref={ref}>{children}</div>
      </div>
    </div>
  );
}

export default FilterWidget;
