import React, { useState } from "react";
import Products from "../Products";
import "./index.css";

function Tabs() {
  const [tab, setTab] = useState("must_have");

  function changeTab(tab) {
    setTab(tab);
  }

  return (
    <div>
      <ul className="Tabs">
        <li
          className="Tabs__item--current"
          onClick={() => {
            changeTab("must_have");
          }}
        >
          <h4>must have</h4>
        </li>
        <li
          className="Tabs__item"
          onClick={() => {
            changeTab("best_seller");
          }}
        >
          <h4>best seller</h4>
        </li>
        <li
          className="Tabs__item"
          onClick={() => {
            changeTab("special_offers");
          }}
        >
          <h4>special offers</h4>
        </li>
      </ul>
      <Products />
    </div>
  );
}

export default Tabs;
