import React, { useContext } from "react";
import { CartContext } from "../../../contexts/CartContext";

function CartWidget() {
  const { cart } = useContext(CartContext);

  return (
    <div className="CartWidget">
      <div className="CartWidget__items">
        <span className="CartWidget__number">{cart.items.length || "10"}</span>
        <span className="CartWidget__label">items</span>
      </div>
      <div className="CartWidget__total">
        <span className="CartWidget__currency">$</span>
        <span className="CartWidget__integer">{cart.total || "10"}</span>
        {/* <span className="CartWidget__decimal">50</span> */}
      </div>
    </div>
  );
}

export default CartWidget;
