import React, { cloneElement } from "react";
import useShow from "../../hooks/useShow";
import "./index.scss";

function FilterWidget(props) {
  // function FilterWidget({ children, title, render }) {
  const { ref, style, show, setContent } = useShow();

  return (
    <div className="filter-widget">
      <h2 className="filter-widget__title" onClick={show}>
        {props.title}
      </h2>
      <div style={{ ...style }}>
        <div style={{ marginBottom: "30px" }} ref={ref}>
          {props.render({ setContent: setContent })}
          {/* {children} */}
          {/* {cloneElement(children[0], { setContent: setContent })} */}
        </div>
      </div>
    </div>
  );
}

export default FilterWidget;
