function productsReducer(state, { type, payload }) {
  switch (type) {
    case "FETCH_PRODUCTS":
      return {
        ...state,
        products: payload,
      };
    case "FETCH_CATEGORIES":
      return {
        ...state,
        categories: payload,
      };
    case "FILTER_BY_CATEGORY":
      const filteredProducts = state.products.filter(
        (product) => product.category === payload
      );

      return {
        ...state,
        filteredProducts,
      };

    default:
      return state;
  }
}

export default productsReducer;
