import React, { useContext } from "react";
import BlogNewsWidgetItem from "./BlogNewsWidgetItem";
import { BlogContext } from "../../contexts/BlogContext";

function BlogNewsWidget() {
  const { posts } = useContext(BlogContext);

  return (
    <div>
      {posts.map((post) => (
        <BlogNewsWidgetItem key={post.id} post={post} />
      ))}
    </div>
  );
}

export default BlogNewsWidget;
