import React from "react";
import "./index.scss";

function CtaWidget() {
  return (
    <div className="CtaWidget">
      <p className="CtaWidget__text">
        Stay updated with Bazar to enjoy our special promotions!
      </p>
      <form action="">
        {/* <div className="CtaWidget__input"> */}
        <input
          className="CtaWidget__field"
          type="text"
          placeholder="insert your email address"
        />
        {/* </div> */}
        <div className="CtaWidget__submit-wrapper">
          <input
            className="CtaWidget__submit"
            type="submit"
            value="subscribe"
          />
        </div>
      </form>
    </div>
  );
}

export default CtaWidget;
