import React, { createContext, useEffect, useReducer } from "react";
import blogReducer from "./blogReducer";
import fetchApi from "../../services/fetchApi";
import actions from "./actions";

const initialState = {
  fetchError: {},
  isLoading: false,
  posts: [],
  post: {},
  testimonials: [],
};

export const BlogContext = createContext(initialState);

function BlogContextProvider({ children }) {
  const [state, dispatch] = useReducer(blogReducer, initialState);

  function fetchPost(id) {
    const post = fetchApi(`http://localhost:3004/posts/${id}`);
    // post.then((post) => {
    //   dispatch({ type: actions.FETCH_POST, payload: post });
    // });
    //   .catch((error) => {
    //     console.log(error);
    //   });
  }

  useEffect(() => {
    dispatch({ type: actions.IS_LOADING, payload: true });
    const posts = fetchApi("http://localhost:3004/posts");
    posts
      .then((posts) => {
        dispatch({ type: actions.FETCH_POSTS, payload: posts });
        dispatch({ type: actions.IS_LOADING, payload: false });
      })
      .catch((error) => {
        dispatch({ type: actions.FETCH_ERROR, payload: error });
        dispatch({ type: actions.IS_LOADING, payload: false });
      });
  }, []);

  useEffect(() => {
    const testimonials = fetchApi("http://localhost:3004/testimonials");
    testimonials
      .then((posts) => {
        dispatch({ type: actions.FETCH_TESTIMONIALS, payload: posts });
      })
      .catch((error) => {});
  }, []);

  return (
    <BlogContext.Provider
      value={{
        fetchError: state.fetchError,
        isLoading: state.isLoading,
        posts: state.posts,
        post: state.post,
        fetchPost,
        testimonials: state.testimonials,
      }}
    >
      {children}
    </BlogContext.Provider>
  );
}

export default BlogContextProvider;
