import React, { createContext, useEffect, useReducer, useRef } from "react";
import productsReducer from "./productsReducer";
import fetchApi from "../../services/fetchApi";
import actions from "./actions";
import useFetch from "../../hooks/useFetch";

const initialState = {
  isLoadingProducts: false,
  isLoadingCategories: false,
  error: "",
  products: [],
  filteredProducts: [],
  categories: [],
};

export const ProductsContext = createContext(initialState);

function ProductsContextProvider({ children }) {
  const [state, dispatch] = useReducer(productsReducer, initialState);
  const filterByCategory = useRef(() => {});

  filterByCategory.current = (category) => {
    dispatch({
      type: actions.FILTER_BY_CATEGORY,
      payload: category,
    });
  };

  const [errorProducts, products, isLoading] = useFetch(
    "http://localhost:3004/products"
  );

  const [errorCategories, categories, isLoadingCategories] = useFetch(
    "http://localhost:3004/categories"
  );

  useEffect(() => {
    dispatch({ type: actions.FETCH_PRODUCTS, payload: products });
    dispatch({ type: actions.FETCH_ERROR, payload: errorProducts });
    dispatch({ type: actions.LOADING_PRODUCTS, payload: isLoading });
  }, [errorProducts, products, isLoading]);

  useEffect(() => {
    dispatch({ type: actions.FETCH_CATEGORIES, payload: categories });
    dispatch({ type: actions.FETCH_ERROR, payload: errorCategories });
    dispatch({
      type: actions.LOADING_CATEGORIES,
      payload: isLoadingCategories,
    });
  }, [errorCategories, categories, isLoadingCategories]);

  // useEffect(() => {
  //   dispatch({ type: actions.LOADING_PRODUCTS, payload: true });
  //   dispatch({ type: actions.LOADING_CATEGORIES, payload: true });
  //   const products = fetchApi("http://localhost:3004/products");
  //   products
  //     .then((products) => {
  //       dispatch({ type: actions.FETCH_PRODUCTS, payload: products });
  //       dispatch({ type: actions.LOADING_PRODUCTS, payload: false });
  //     })
  //     .catch((error) => {
  //       dispatch({ type: actions.FETCH_ERROR, payload: error.message });
  //     });
  //   const categories = fetchApi("http://localhost:3004/categories");
  //   categories
  //     .then((products) => {
  //       dispatch({ type: actions.FETCH_CATEGORIES, payload: products });
  //       dispatch({ type: actions.LOADING_CATEGORIES, payload: false });
  //     })
  //     .catch((error) => {
  //       dispatch({ type: actions.FETCH_ERROR, payload: error.message });
  //     });
  // }, []);

  return (
    <ProductsContext.Provider
      value={{
        isLoadingProducts: state.loadingProducts,
        isLoadingCategories: state.loadingCategories,
        error: state.error,
        products: state.products,
        filteredProducts: state.filteredProducts,
        categories: state.categories,
        filterByCategory: filterByCategory.current,
      }}
    >
      {children}
    </ProductsContext.Provider>
  );
}

export default ProductsContextProvider;
