import React, { createContext, useEffect, useReducer } from "react";
import productsReducer from "./productsReducer";
import fetchApi from "../../services/fetchApi";

const initialState = { products: [], filteredProducts: [], categories: [] };

export const ProductsContext = createContext(initialState);

function ProductsContextProvider({ children }) {
  const [state, dispach] = useReducer(productsReducer, initialState);

  function filterByCategory(category) {
    dispach({
      type: "FILTER_BY_CATEGORY",
      payload: category,
    });
  }

  useEffect(() => {
    const data = fetchApi("http://localhost:3004/products");
    data
      .then((products) => {
        dispach({ type: "FETCH_PRODUCTS", payload: products });
      })
      .catch((error) => {
        console.log(error);
      });
    const categories = fetchApi("http://localhost:3004/categories");
    categories
      .then((products) => {
        dispach({ type: "FETCH_CATEGORIES", payload: products });
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <ProductsContex.Provider
      value={{
        products: state.products,
        filteredProducts: state.filteredProducts,
        categories: state.categories,
        filterByCategory,
      }}
    >
      {children}
    </ProductsContex.Provider>
  );
}

export default ProductsContexProvider;
