import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../../contexts/CartContext";
import AddToCart from "../AddToCart";
import "./index.scss";

function QuantityChanger({ product, id, quantity = 0, isAddToCart = false }) {
  const [value, setValue] = useState(quantity || 1);
  const { cart, changeQuantity, updateCart } = useContext(CartContext);

  // useEffect(() => {
  //   setValue(quantity);
  // }, [quantity]);

  useEffect(() => {
    if (cart.isUpdate) changeQuantity(id, parseInt(value));
    updateCart(false);
  }, [cart.isUpdate]);

  return (
    <div className="quantity-changer">
      <div className="quantity__form">
        <button
          className="quantity-changer__button"
          onClick={() => {
            setValue((prevValue) => parseInt(prevValue) - 1);
          }}
        >
          -
        </button>
        <input
          className="quantity-changer__input"
          onChange={(e) => {
            setValue(parseInt(e.target.value));
          }}
          type="number"
          value={value}
        />
        <button
          className="quantity-changer__button"
          onClick={() => {
            setValue((prevValue) => parseInt(prevValue) + 1);
          }}
        >
          +
        </button>
      </div>
      {isAddToCart && <AddToCart quantity={value + quantity} item={product} />}
    </div>
  );
}

export default QuantityChanger;
