import { useContext } from "react";
import { useRouteMatch } from "react-router-dom";
import { ProductsContext } from "../../../contexts/ProductsContext";
import Subcategory from "../Subcategory";
import "./index.scss";

function Category({ category: { category }, subcategories }) {
  const { url } = useRouteMatch();
  const { filterByCategory } = useContext(ProductsContext);

  return (
    <div className="category">
      {/* <Link className="category__link" to={`${url}/category/${category}`}> */}
      {category}
      {/* </Link> */}
      <ul className="category__subcategory">
        {subcategories.map((subcategory, index) => (
          <li
            key={index}
            onClick={() => {
              filterByCategory.current(category);
            }}
          >
            <Subcategory subcategory={subcategory} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Category;
