  const { filterByCategory } = useContext(ProductsContext);
  const { url } = useRouteMatch();

  return (
    <div>
      <Link to={`${url}/${category}`}>{category}</Link>
      {/* {subcategories.map((subcategory, index) => (
        <span
          key={index}
          onClick={() => {
            filterByCategory(subcategory);
          }}
        >
          {subcategory}
        </span>
      ))} */}
    </div>
  );
}

export default Category;
