import React, { useState } from "react";
import CartView from "../CartView";

function CartControl() {
  const [show, setShow] = useState(false);

  return (
    <div
      className="cart-control"
      onMouseEnter={() => {
        setShow(true);
      }}
      onMouseLeave={() => {
        setShow(false);
      }}
    >
      empty cart
      <CartView show={show} setShow={setShow} />
    </div>
  );
}

export default CartControl;
