import React from "react";
import { Link } from "react-router-dom";
import AddToCart from "../AddToCart";
// import "./index.scss";

function ProductItem({ product }) {
  return (
    <div className="ProductThumbnail">
      <div className="ProductThumbnail__group">
        <Link to={`/product/${product.id}`}>
          <img className="ProductThumbnail__img" src={product.img} alt="" />
        </Link>
        <div className="ProductThumbnail__meta">
          <h3 className="ProductThumbnail__heading">{product.name}</h3>
          <span className="ProductThumbnail__currency">$</span>
          <span className="ProductThumbnail__price">{product.price}</span>
        </div>
        <AddToCart item={product} />
        <div className="ProductThumbnail__actions">
          <a className="ProductThumbnail__details" href="/">
            Details
          </a>
          <a className="ProductThumbnail__wishlist" href="/">
            wishlist
          </a>
          <a className="ProductThumbnail__compare" href="/">
            compare
          </a>
          <a className="ProductThumbnail__share" href="/">
            share
          </a>
        </div>
      </div>
    </div>
  );
}

export default ProductItem;
