import { useEffect, useRef, useState } from "react";

function useShow() {
  const ref = useRef(null);
  const [height, setHeight] = useState(window.innerWidth > 768 ? "initial" : 0);
  const [isShow, setIsShow] = useState(false);
  const style = {
    height,
    overflow: "hidden",
    transition: "height 0.3s ease-in-out",
  };
  console.log(height);

  function show() {
    setIsShow((prevIsShow) => {
      return !prevIsShow;
    });
  }

  useEffect(() => {
    if (isShow) {
      setHeight(
        ref.current.clientHeight +
          parseInt(getComputedStyle(ref.current).marginBottom)
      );
      return;
    }
    if (height === "initial") {
      return;
    }
    setHeight(0);
  }, [isShow, height]);

  return { ref, style, show };
}

export default useShow;
