import { useEffect, useRef, useState } from "react";

export default function useSlider(items, transitions, slider) {
  const [counter, setCounter] = useState(1);
  const [isAuto, setIsAuto] = useState(true);
  const [newSlides, setNewSlides] = useState([]);
  let bbb = [];

  useEffect(() => {
    const aaa = [
      { ...items[items.length - 1] },
      { ...items[0] },
      { ...items[1] },
    ];
    setNewSlides(aaa);
    bbb = [...aaa];
  }, [items]);

  useEffect(() => {
    // console.log(newSlides);
  }, [newSlides]);

  const newItems = [{ ...items[items.length - 1] }, ...items, { ...items[0] }];
  const auto = useRef();
  const moveNext = useRef(() => {});
  const makeInterval = useRef(() => {});

  function movePrev() {
    setCounter((prevCounter) => {
      if (prevCounter === 0) {
        return prevCounter;
      }
      return prevCounter - 1;
    });
  }

  moveNext.current = () => {
    // slider.current.style.transition = transitions.auto;
    setCounter((prevCounter) => {
      if (counter >= newItems.length - 1) {
        return 1;
      }
      return prevCounter + 1;
    });
    bbb = [...newSlides];
    bbb.shift();
    bbb.push({ ...items[counter + 1] });
    // setNewSlides((prevItems) => {
    // console.log(prevItems);
    // return prevItems;
    // });
  };

  function clickPrev() {
    setIsAuto(false);
    slider.current.style.transition = transitions.manual;
    movePrev();
  }

  function clickNext() {
    // console.log(newSlides);
    setIsAuto(false);
    slider.current.style.transition = transitions.manual;
    moveNext.current();
  }

  function transitionEnd() {
    if (counter === 0) {
      slider.current.style.transition = transitions.end;
      setCounter(newItems.length - 2);
      return;
    }

    if (counter === 5) {
      slider.current.style.transition = transitions.end;
      setCounter(1);
      return;
    }

    console.log(bbb);
    setNewSlides(bbb);
  }

  makeInterval.current = () => {
    console.log("make interval");
    auto.current = setInterval(() => {
      slider.current.style.transition = transitions.auto;
      moveNext.current();
    }, 3000);
  };

  useEffect(() => {
    if (isAuto) {
      // makeInterval.current();
    } else {
      clearInterval(auto.current);
    }
    setIsAuto(true);
    return () => {
      clearInterval(auto);
    };
  }, [isAuto, makeInterval]);

  useEffect(() => {
    // let offset = 100 * counter;
    let offset = 100;
    slider.current.style.transform = `translateX(-${offset}%)`;
  }, [counter, slider]);

  return {
    transitionEnd,
    clickPrev,
    clickNext,
    items: newSlides,
  };
}
