import React, { useContext, useState } from "react";
import CategoriesFilter from "../../components/CategoriesFilter";
import PriceFilter from "../../components/PriceFilter";
import Products from "../../components/Products";
import { ProductsContext } from "../../contexts/ProductsContext";
import usePagination from "../../hooks/usePagination";
import FilterWidget from "../../shared/FilterWidget";
import Paginatation from "../../shared/Pagination";
import "./index.scss";

function ShopPage() {
  // const { path } = useRouteMatch();
  const { filteredProducts } = useContext(ProductsContext);
  const itemsPerPage = 9;

  const { currentItems, currentPage, paginate } = usePagination(
    filteredProducts,
    itemsPerPage
  );

  return (
    <div className="shop-page">
      <div className="shop-page__sidebar">
        <FilterWidget title="filter by categories">
          <CategoriesFilter />
        </FilterWidget>
        <FilterWidget title="filter by price">
          <PriceFilter />
        </FilterWidget>
        <FilterWidget title="color"></FilterWidget>
      </div>
      <div className="shop-page__content">
        <div className="shop-page__items">
          <Products products={currentItems} />
        </div>
        <div className="shop-page__pagination">
          <Paginatation
            itemsPerPage={itemsPerPage}
            totalItems={filteredProducts.length}
            paginate={paginate}
            currentPage={currentPage}
          />
        </div>
      </div>
      {/* <Switch>
        <Route path={path} exact component={Products} />
        <Route
          path={`${path}/category/:category`}
          component={FilteredProducts}
        />
        <Route
          path={`${path}/search/:searchedProduct`}
          component={SearchPage}
        />
      </Switch> */}
    </div>
  );
}

export default ShopPage;
