import { getTotal } from "../../services/products";
import actions from "./actions";

function cartReducer(state, { type, payload }) {
  switch (type) {
    case actions.ADD_ITEM:
      return {
        ...state,
        total: getTotal(state.items),
        items: [...state.items, { ...payload, quantity: 1 }],
      };

    case actions.REMOVE_ITEM:
      let newItems = [...state.items];
      newItems = newItems.filter((item) => item.id !== payload);
      return {
        ...state,
        total: getTotal(state.items),
        items: [...newItems],
      };

    case actions.CHANGE_QUANTITY:
      const quantity = payload.quantity;
      const item = state.items.find((item) => item.id === payload.id);
      if (quantity) {
        item.quantity = quantity;
      } else {
        item.quantity++;
      }
      return {
        ...state,
        total: getTotal(state.items),
      };

    case "UPDATE_CART":
      return {
        ...state,
        isUpdate: payload,
      };

    default:
      if (!type) {
        throw new Error("cartReducer: You must set type of action");
      }
      throw new Error(`cartReducer: Cant find action of this type: ${type}`);
  }
}

export default cartReducer;
