import actions from "./actions";

function blogReducer(state, { type, payload }) {
  switch (type) {
    case actions.FETCH_POSTS:
      console.log(payload);
      return {
        ...state,
        posts: payload,
      };
    case actions.FETCH_POST:
      return {
        ...state,
        post: payload,
      };
    default:
      if (!type) {
        throw new Error("You must set type of action");
      }
      throw new Error("No action");
  }
}

export default blogReducer;
