import React, { useContext, useRef } from "react";
import { BlogContext } from "../../contexts/BlogContext";
import "./index.scss";

// function TestimonialWidget() {
//   const { testimonials } = useContext(BlogContext);
//   console.log(testimonials);
//   return <div></div>;
// }

// export default TestimonialWidget;

const transitions = {
  manual: "all 1s ease-in-out 0.01s",
  auto: "all 1s ease-in-out 1s",
  end: "all 0s ease-in-out 0.01s",
};

function TestimonialWidget() {
  const { testimonials } = useContext(BlogContext);
  const slider = useRef();
  const { transitionEnd, clickPrev, clickNext, items } = useSlider(
    testimonials,
    transitions,
    slider
  );
  return (
    <div className="TestimonialWidget">
      <div className="slider" ref={slider} onTransitionEnd={transitionEnd}>
        {items &&
          items.map((testimonial, index) => (
            <Slide testimonial={testimonial} key={index} />
          ))}
      </div>
      <Nav clickPrev={clickPrev} clickNext={clickNext} />
    </div>
  );
}

export default TestimonialWidget;

function Slide({ testimonial }) {
  return (
    <div className="slide">
      <blockquote className="TestimonialWidget__content">
        {testimonial.content}
      </blockquote>
      <p className="TestimonialWidget__author">{testimonial.id}</p>
    </div>
  );
}

function Nav({ clickNext, clickPrev }) {
  return (
    <ul className="TestimonialWidget__nav">
      <li
        className="TestimonialWidget__prev"
        onClick={() => {
          clickPrev();
        }}
      >
        Prev
      </li>
      <li
        className="TestimonialWidget__next"
        onClick={() => {
          clickNext();
        }}
      >
        Next
      </li>
    </ul>
  );
}
