import React from "react";
// import "./index.scss";

function RecentPost({ post }) {
  // const date = post.date.split("-");
  const date = "";
  return (
    <div className="RecentPost">
      <p className="RecentPost__date">
        {date[1]}
        <span className="RecentPost__month">{date[0]}</span>
      </p>
      <div className="RecentPost__border">
        <img className="RecentPost__thumb" src={post.thumb} alt="" />

        <a className="RecentPost__link" href="/">
          {post.title}
        </a>
        <p className="RecentPost__author">posted by {post.author}</p>
      </div>
    </div>
  );
}

export default RecentPost;
