import React from "react";
import "./index.scss";

function HomeWidget({ children, title, isOrange }) {
  const orangeClass = isOrange ? "home-widget__heading--orange" : "";
  return (
    <div className="home-widget">
      <div className="home-widget__border">
        <h3 className={`home-widget__heading ${orangeClass}`}>{title}</h3>
        {children}
      </div>
    </div>
  );
}

export default HomeWidget;
