import React from "react";
import PostHeader from "./PostHeader";
import BlogWidgetItem from "../../components/BlogWidget/BlogWidgetItem";

function Post({ post, readme }) {
  return (
    <div>
      <BlogWidgetItem post={post} />
      <p dangerouslySetInnerHTML={{ __html: readme || post.content }}></p>
    </div>
  );
}

export default Post;
