import React from "react";
import { Route, Switch, useRouteMatch } from "react-router-dom";
import CategoriesFilter from "../../components/CategoriesFilter";
import FilteredProducts from "../../components/FilteredProducts";
import FilterWidget from "../../components/FilterWidget";
import Products from "../../components/Products";

function ShopPage(props) {
  const { path } = useRouteMatch();
  console.log(props.match);
  return (
    <div>
      {console.log("render")}
      shop page
      <FilterWidget title="filter by categories">
        <CategoriesFilter />
      </FilterWidget>
      <FilterWidget title="filter by price"></FilterWidget>
      <FilterWidget title="color"></FilterWidget>
      <Switch>
        <Route path={path} exact component={Products} />
        <Route path={`${path}/:category`} component={FilteredProducts} />
      </Switch>
    </div>
  );
}

export default ShopPage;
