import React from "react";

function EntryPost({ post }) {
  const readme = post.content.split(" ").slice(0, 73).join(" ");
  return (
    <div>
      {post.date}
      {post.author}
      {post.title}
      <p dangerouslySetInnerHTML={{ __html: readme }}></p>
    </div>
  );
}

export default EntryPost;
