import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../../../context/CartContext";
import QuantityCahnger from "../../../../shared/QuantityChanger";

function CartRow({ item: { id, name, price, quantity } }) {
  return (
    <tr key={id}>
      <td>{name}</td>
      <td>{price}</td>
      <td>
        <QuantityCahnger id={id} quantity={quantity} />
      </td>
      <td>{quantity * price}</td>
    </tr>
  );
}

export default CartRow;
