import React, { useContext } from "react";
import { Link, useRouteMatch } from "react-router-dom";
import { ProductsContext } from "../../contexts/ProductsContext";
import Loading from "../../shared/Loading";

function CategoriesFilter() {
  const { isLoadingCategories, categories } = useContext(ProductsContext);

  return (
    <div>
      CategoriesFilter
      {isLoadingCategories ? (
        <Loading />
      ) : (
        categories.map((category, index) => (
          <Category
            key={index}
            category={category}
            subcategories={category.subcategories}
          />
        ))
      )}
    </div>
  );
}

export default CategoriesFilter;

function Category({ category: { category }, subcategories }) {
  const { filterByCategory } = useContext(ProductsContext);
  const { url } = useRouteMatch();

  return (
    <div>
      <Link to={`${url}/${category}`}>{category}</Link>
      {subcategories.map((subcategory, index) => (
        <span
          key={index}
          onClick={() => {
            filterByCategory(subcategory);
          }}
        >
          {subcategory}
        </span>
      ))}
    </div>
  );
}
