import React, { useEffect } from "react";
import useShow from "../../hooks/useShow";
import "./index.scss";

function FilterWidget({ children, title }) {
  const { ref, style, show } = useShow();
  useEffect(() => {
    console.log(ref.current.clientHeigt);
  }, [ref.current.clientHeigt]);

  return (
    <div className="filter-widget">
      <h2 className="filter-widget__title" onClick={show}>
        {title}
      </h2>
      <div style={{ ...style }}>
        <div style={{ marginBottom: "30px" }} ref={ref}>
          {children}
        </div>
      </div>
    </div>
  );
}

export default FilterWidget;
