import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ProductsContext } from "../../contexts/ProductsContext";
import ProductItem from "../../shared/ProductItem";
import ProductsList from "../../shared/ProductsList";

function SearchedProducts() {
  const { products } = useContext(ProductsContext);
  const { searchedProduct } = useParams();
  const [searchedProducts, setSearchedProducts] = useState([]);

  useEffect(() => {
    const searchedProducts = products.filter((product) => {
      return product.name.toLowerCase().includes(searchedProduct.toLowerCase());
    });
    setSearchedProducts(searchedProducts);
  }, [searchedProduct, products]);

  return (
    <div>
      Searched products
      {!searchedProducts.length ? (
        <span>No products were found matching your selection.</span>
      ) : (
        <ProductsList products={searchedProducts} />
      )}
    </div>
  );
}

export default SearchedProducts;
