import React from "react";
import { Link } from "react-router-dom";
import "./index.scss";

function BlogWidgetItem({ post }) {
  const date = post.date ? post.date.split("-") : [];
  return (
    <div className="recent-post">
      <p className="recent-post__date">
        {date[1]}
        <span className="recent-post__month">{date[0]}</span>
      </p>
      <div className="recent-post__border">
        <img className="recent-post__thumb" src={post.thumb} alt="" />

        <span className="recent-post__marginTop">
          <Link className="recent-post__link" to={`blog/post/${post.id}`}>
            {post.title}
          </Link>
          <p className="recent-post__author">posted by {post.author}</p>
        </span>
      </div>
    </div>
  );
}

export default BlogWidgetItem;
