import React from "react";
import { Link } from "react-router-dom";
import "./index.scss";

function PostsListItem({ post }) {
  const date = post.date ? post.date.split("-") : [];
  return (
    <div className="post-list-item">
      <p className="post-list-item__date">
        {date[1]}
        <span className="post-list-item__month">{date[0]}</span>
      </p>
      <span className="post-list-item__marginTop">
        <Link className="post-list-item__link" to={`blog/post/${post.id}`}>
          {post.title}
        </Link>
        <p className="post-list-item__author">posted by {post.author}</p>
      </span>
    </div>
  );
}

export default PostsListItem;
